// Blog Social Media Content Generator
const blogPost = $json;

// Generate platform-specific content
const socialContent = {
  facebook: {
    text: `ðŸ  New Blog Post: ${blogPost.title}\n\n${blogPost.meta_description}\n\nPerfect for anyone looking to transform their ${blogPost.category} space! âœ¨\n\nRead the full guide: ${blogPost.url || 'https://yourblog.com/posts/' + blogPost.slug}\n\n#InteriorDesign #${blogPost.category.charAt(0).toUpperCase() + blogPost.category.slice(1)} #HomeDecor #DesignTips`,
    image_url: blogPost.featured_image || null,
    link: blogPost.url || 'https://yourblog.com/posts/' + blogPost.slug
  },

  instagram: {
    text: `âœ¨ NEW BLOG POST âœ¨\n\n${blogPost.title}\n\n${blogPost.category === 'wallpapers' ? 'ðŸŽ¨ Transform your walls with stunning wallpaper ideas' : blogPost.category === 'furniture' ? 'ðŸ›‹ï¸ Discover furniture pieces that make a statement' : 'ðŸ¡ Get inspired with fresh home decor ideas'}\n\nSwipe for preview ðŸ‘‰\nLink in bio for full article\n\n${blogPost.keywords.map(k => '#' + k.replace(/\s+/g, '')).join(' ')} #InteriorDesign #HomeDecor`,
    image_url: blogPost.featured_image || null,
    carousel_images: blogPost.gallery_images || []
  },

  pinterest: {
    text: `${blogPost.title} | ${blogPost.meta_description}`,
    board_name: blogPost.category === 'wallpapers' ? 'Wallpaper Ideas' : blogPost.category === 'furniture' ? 'Furniture Inspiration' : 'Home Decor',
    image_url: blogPost.featured_image || null,
    link: blogPost.url || 'https://yourblog.com/posts/' + blogPost.slug
  },

  twitter: {
    text: `ðŸ“– New guide: ${blogPost.title}\n\n${blogPost.meta_description.substring(0, 100)}...\n\n${blogPost.url || 'https://yourblog.com/posts/' + blogPost.slug}\n\n${blogPost.keywords.slice(0, 3).map(k => '#' + k.replace(/\s+/g, '')).join(' ')}`,
    image_url: blogPost.featured_image || null
  },

  linkedin: {
    text: `Professional insight: ${blogPost.title}\n\n${blogPost.meta_description}\n\nAs interior design professionals, we understand the importance of staying current with trends and techniques. This comprehensive guide covers everything you need to know about ${blogPost.category}.\n\nRead more: ${blogPost.url || 'https://yourblog.com/posts/' + blogPost.slug}\n\n#InteriorDesign #ProfessionalDesign #${blogPost.category}`,
    image_url: blogPost.featured_image || null
  }
};

// Add blog metadata
const enrichedContent = {
  ...blogPost,
  social_content: socialContent,
  publish_time: new Date().toISOString(),
  platforms_to_post: ['facebook', 'instagram', 'pinterest'],
  scheduled_posts: {
    immediate: ['facebook'],
    delayed_1h: ['instagram'],
    delayed_2h: ['pinterest'],
    delayed_4h: ['twitter', 'linkedin']
  }
};

return [{ json: enrichedContent }];

// Check for scheduled social media posts
const now = new Date();
const oneHourAgo = new Date(now.getTime() - 60 * 60 * 1000);
const twoHoursAgo = new Date(now.getTime() - 2 * 60 * 60 * 1000);
const fourHoursAgo = new Date(now.getTime() - 4 * 60 * 60 * 1000);

// Mock data - in real implementation, this would check a database
// for scheduled posts that need to be published
const scheduledPosts = [
  {
    platform: 'twitter',
    scheduled_time: fourHoursAgo.toISOString(),
    content: 'Sample Twitter post about latest blog',
    status: 'pending'
  },
  {
    platform: 'linkedin',
    scheduled_time: fourHoursAgo.toISOString(),
    content: 'Sample LinkedIn post about latest blog',
    status: 'pending'
  }
];

// Filter posts that are ready to be published
const readyToPosts = scheduledPosts.filter(post => {
  const postTime = new Date(post.scheduled_time);
  return postTime <= now && post.status === 'pending';
});

return readyToPosts.map(post => ({ json: post }));

// Manual Social Media Scheduling
const request = $json;

// Validate request
if (!request.content || !request.platforms) {
  throw new Error('Content and platforms are required');
}

// Process manual scheduling request
const scheduledPost = {
  content: request.content,
  platforms: request.platforms, // array of platform names
  scheduled_time: request.scheduled_time || new Date().toISOString(),
  image_url: request.image_url || null,
  link: request.link || null,
  hashtags: request.hashtags || [],
  priority: request.priority || 'normal',
  campaign: request.campaign || 'manual',
  created_by: request.created_by || 'system',
  status: 'scheduled'
};

// Generate platform-specific content
const platformContent = {};
request.platforms.forEach(platform => {
  switch(platform) {
    case 'facebook':
      platformContent.facebook = {
        text: request.content + (request.link ? `\n\n${request.link}` : ''),
        image_url: request.image_url
      };
      break;
    case 'instagram':
      platformContent.instagram = {
        text: request.content + (request.hashtags.length ? `\n\n${request.hashtags.join(' ')}` : ''),
        image_url: request.image_url
      };
      break;
    case 'twitter':
      platformContent.twitter = {
        text: request.content.substring(0, 240) + (request.link ? ` ${request.link}` : ''),
        image_url: request.image_url
      };
      break;
    case 'pinterest':
      platformContent.pinterest = {
        text: request.content,
        image_url: request.image_url,
        link: request.link
      };
      break;
  }
});

return [{ json: { ...scheduledPost, platform_content: platformContent } }];
