parameters.functionCode:
// Invoice Generator
const order = $json;

// Generate invoice number
const invoiceNumber = `INV-${new Date().getFullYear()}-${String(Date.now()).slice(-6)}`;

// Calculate totals
let subtotal = 0;
let taxAmount = 0;
const taxRate = 0.08; // 8% tax rate

const invoiceItems = [];
if (order.items) {
  order.items.forEach(item => {
    const itemTotal = (item.price || 0) * (item.quantity || 1);
    subtotal += itemTotal;

    invoiceItems.push({
      name: item.name || item.product_name,
      description: item.description || '',
      quantity: item.quantity || 1,
      unit_price: item.price || 0,
      total: itemTotal
    });
  });
}

taxAmount = subtotal * taxRate;
const shippingCost = order.shipping_cost || 0;
const total = subtotal + taxAmount + shippingCost;

// Generate invoice data
const invoice = {
  invoice_number: invoiceNumber,
  order_id: order.order_id || order.id,
  customer: {
    name: order.customer_name || order.customer?.name || 'Customer',
    email: order.customer_email || order.customer?.email || order.email,
    address: order.billing_address || order.address || {}
  },
  items: invoiceItems,
  subtotal: subtotal.toFixed(2),
  tax_rate: (taxRate * 100).toFixed(0),
  tax_amount: taxAmount.toFixed(2),
  shipping_cost: shippingCost.toFixed(2),
  total: total.toFixed(2),
  currency: order.currency || 'USD',
  issue_date: new Date().toISOString().split('T')[0],
  due_date: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0], // 30 days from now
  status: order.status || 'pending',
  payment_method: order.payment_method || 'Credit Card',
  notes: order.notes || 'Thank you for your business!'
};

return [{ json: invoice }];

