// Lead Scoring Algorithm
const lead = $json;
let score = 0;

// Email domain scoring
if (lead.email) {
  const domain = lead.email.split('@')[1];
  const businessDomains = ['gmail.com', 'yahoo.com', 'hotmail.com'];
  if (!businessDomains.includes(domain)) {
    score += 20; // Business email
  }
}

// Source scoring
switch (lead.source) {
  case 'organic':
    score += 30;
    break;
  case 'paid':
    score += 25;
    break;
  case 'social':
    score += 15;
    break;
  case 'referral':
    score += 35;
    break;
  default:
    score += 10;
}

// Behavior scoring
if (lead.pages_visited > 5) score += 20;
if (lead.time_on_site > 300) score += 15; // 5+ minutes
if (lead.downloaded_resource) score += 25;
if (lead.viewed_pricing) score += 30;

// Company size (if provided)
if (lead.company_size) {
  if (lead.company_size === 'enterprise') score += 40;
  else if (lead.company_size === 'medium') score += 25;
  else if (lead.company_size === 'small') score += 15;
}

// Budget indication
if (lead.budget) {
  if (lead.budget === 'high') score += 35;
  else if (lead.budget === 'medium') score += 20;
  else if (lead.budget === 'low') score += 5;
}

// Determine lead quality
let quality = 'cold';
if (score >= 80) quality = 'hot';
else if (score >= 50) quality = 'warm';

return [{
  json: {
    ...lead,
    score: score,
    quality: quality,
    scored_at: new Date().toISOString()
  }
}];
