parameters.functionCode:
// Order Processing Logic
const order = $json;

// Calculate order metrics
const orderValue = order.total || 0;
const itemCount = order.items ? order.items.length : 0;
const isHighValue = orderValue > 500;
const isFirstOrder = order.customer_order_count === 1;

// Determine order priority
let priority = 'normal';
if (isHighValue) priority = 'high';
if (orderValue > 1000) priority = 'urgent';

// Calculate estimated processing time
let processingTime = '1-2 business days';
if (priority === 'high') processingTime = '24 hours';
if (priority === 'urgent') processingTime = '12 hours';

// Check for special handling requirements
const specialHandling = [];
if (order.items) {
  order.items.forEach(item => {
    if (item.fragile) specialHandling.push('fragile');
    if (item.oversized) specialHandling.push('oversized');
    if (item.perishable) specialHandling.push('perishable');
  });
}

// Generate order summary
const processedOrder = {
  ...order,
  order_id: order.id || order.order_id,
  processed_at: new Date().toISOString(),
  priority: priority,
  estimated_processing: processingTime,
  special_handling: specialHandling,
  is_high_value: isHighValue,
  is_first_order: isFirstOrder,
  item_count: itemCount,
  needs_review: isHighValue || specialHandling.length > 0,
  customer_email: order.customer?.email || order.email,
  customer_name: order.customer?.name || order.billing_address?.name || 'Customer',
  shipping_address: order.shipping_address || order.address
};

return [{ json: processedOrder }];

