// Sales Report Generator
const today = new Date();
const yesterday = new Date(today);
yesterday.setDate(yesterday.getDate() - 1);

// Mock sales data - in real implementation, this would fetch from your database
const salesData = {
  date: yesterday.toISOString().split('T')[0],
  total_orders: Math.floor(Math.random() * 50) + 20,
  total_revenue: (Math.random() * 5000 + 1000).toFixed(2),
  avg_order_value: 0,
  top_products: [
    { name: 'Premium Widget', sales: Math.floor(Math.random() * 20) + 5, revenue: (Math.random() * 1000 + 200).toFixed(2) },
    { name: 'Deluxe Gadget', sales: Math.floor(Math.random() * 15) + 3, revenue: (Math.random() * 800 + 150).toFixed(2) },
    { name: 'Standard Tool', sales: Math.floor(Math.random() * 25) + 8, revenue: (Math.random() * 600 + 100).toFixed(2) }
  ],
  new_customers: Math.floor(Math.random() * 15) + 5,
  returning_customers: Math.floor(Math.random() * 35) + 15,
  conversion_rate: (Math.random() * 3 + 2).toFixed(2),
  traffic_sources: {
    organic: Math.floor(Math.random() * 40) + 30,
    paid: Math.floor(Math.random() * 25) + 15,
    social: Math.floor(Math.random() * 20) + 10,
    direct: Math.floor(Math.random() * 30) + 20,
    referral: Math.floor(Math.random() * 15) + 5
  }
};

// Calculate average order value
salesData.avg_order_value = (salesData.total_revenue / salesData.total_orders).toFixed(2);

// Calculate week-over-week growth (mock)
const weekGrowth = {
  orders: ((Math.random() - 0.5) * 20).toFixed(1),
  revenue: ((Math.random() - 0.5) * 25).toFixed(1),
  conversion: ((Math.random() - 0.5) * 1).toFixed(2)
};

const report = {
  ...salesData,
  week_over_week: weekGrowth,
  generated_at: new Date().toISOString()
};

return [{ json: report }];
