parameters.functionCode:
// SEO Performance Analysis
const items = $input.all();
const analysis = {
  totalClicks: 0,
  totalImpressions: 0,
  avgPosition: 0,
  topQueries: [],
  topPages: [],
  issues: []
};

// Process Search Console data
if (items[0] && items[0].json) {
  const data = items[0].json;

  if (data.rows) {
    data.rows.forEach(row => {
      analysis.totalClicks += row.clicks || 0;
      analysis.totalImpressions += row.impressions || 0;

      if (row.keys && row.keys[0]) {
        analysis.topQueries.push({
          query: row.keys[0],
          clicks: row.clicks,
          impressions: row.impressions,
          ctr: row.ctr,
          position: row.position
        });
      }
    });

    // Calculate average position
    analysis.avgPosition = data.rows.reduce((sum, row) => sum + (row.position || 0), 0) / data.rows.length;

    // Sort top queries by clicks
    analysis.topQueries.sort((a, b) => b.clicks - a.clicks);
    analysis.topQueries = analysis.topQueries.slice(0, 10);

    // Identify issues
    if (analysis.avgPosition > 10) {
      analysis.issues.push('Average position is below 10');
    }

    if (analysis.totalClicks < 100) {
      analysis.issues.push('Low click volume (< 100 clicks/week)');
    }

    const avgCTR = analysis.totalClicks / analysis.totalImpressions;
    if (avgCTR < 0.02) {
      analysis.issues.push('Low CTR (< 2%)');
    }
  }
}

return [{ json: analysis }];

