parameters.functionCode:
// Competitor Analysis
const items = $input.all();
const competitors = [];
const targetDomain = 'yourstore.com';

if (items[0] && items[0].json && items[0].json.organic_results) {
  const results = items[0].json.organic_results;

  results.forEach((result, index) => {
    const domain = new URL(result.link).hostname;

    if (domain !== targetDomain) {
      competitors.push({
        domain: domain,
        position: index + 1,
        title: result.title,
        snippet: result.snippet,
        url: result.link
      });
    } else {
      // Found our site
      competitors.push({
        domain: targetDomain,
        position: index + 1,
        title: result.title,
        snippet: result.snippet,
        url: result.link,
        isOurSite: true
      });
    }
  });
}

return [{ json: { competitors, query: $input.first().json.query } }];
