// SEO Analysis for Blog Posts
const blogPost = $json;

// SEO scoring algorithm
let seoScore = 0;
const seoChecks = {
  title_length: false,
  title_keyword: false,
  meta_description: false,
  content_length: false,
  keyword_density: false,
  headings: false,
  internal_links: false,
  images_alt_text: false,
  url_structure: false,
  readability: false
};

// Title analysis (max 60 chars, includes keyword)
if (blogPost.title && blogPost.title.length <= 60) {
  seoChecks.title_length = true;
  seoScore += 10;
}

if (blogPost.title && blogPost.keywords && 
    blogPost.keywords.some(keyword => 
      blogPost.title.toLowerCase().includes(keyword.toLowerCase()))) {
  seoChecks.title_keyword = true;
  seoScore += 15;
}

// Meta description (150-160 chars)
if (blogPost.meta_description && 
    blogPost.meta_description.length >= 150 && 
    blogPost.meta_description.length <= 160) {
  seoChecks.meta_description = true;
  seoScore += 10;
}

// Content length (minimum 800 words)
const wordCount = blogPost.content ? blogPost.content.split(' ').length : 0;
if (wordCount >= 800) {
  seoChecks.content_length = true;
  seoScore += 15;
}

// Keyword density (1-3%)
if (blogPost.content && blogPost.keywords) {
  const primaryKeyword = blogPost.keywords[0];
  const keywordCount = (blogPost.content.toLowerCase().match(new RegExp(primaryKeyword.toLowerCase(), 'g')) || []).length;
  const density = (keywordCount / wordCount) * 100;

  if (density >= 1 && density <= 3) {
    seoChecks.keyword_density = true;
    seoScore += 10;
  }
}

// Heading structure (H1, H2, H3)
if (blogPost.content) {
  const h1Count = (blogPost.content.match(/^# /gm) || []).length;
  const h2Count = (blogPost.content.match(/^## /gm) || []).length;

  if (h1Count === 1 && h2Count >= 2) {
    seoChecks.headings = true;
    seoScore += 10;
  }
}

// Internal links (at least 2)
if (blogPost.content) {
  const internalLinks = (blogPost.content.match(/\[.*?\]\(https:\/\/yourstore\.com.*?\)/g) || []).length;
  if (internalLinks >= 2) {
    seoChecks.internal_links = true;
    seoScore += 10;
  }
}

// Images with alt text
if (blogPost.images && blogPost.images.length > 0) {
  const imagesWithAlt = blogPost.images.filter(img => img.alt_text).length;
  if (imagesWithAlt === blogPost.images.length) {
    seoChecks.images_alt_text = true;
    seoScore += 10;
  }
}

// URL structure (includes keyword, no special chars)
if (blogPost.slug && blogPost.keywords) {
  const primaryKeyword = blogPost.keywords[0].toLowerCase().replace(/\s+/g, '-');
  if (blogPost.slug.includes(primaryKeyword) && !/[^a-z0-9-]/.test(blogPost.slug)) {
    seoChecks.url_structure = true;
    seoScore += 10;
  }
}

// Readability score (mock calculation)
if (wordCount > 0) {
  const avgWordsPerSentence = wordCount / (blogPost.content.split('.').length || 1);
  if (avgWordsPerSentence <= 20) {
    seoChecks.readability = true;
    seoScore += 10;
  }
}

// Generate recommendations
const recommendations = [];
if (!seoChecks.title_length) recommendations.push('Shorten title to under 60 characters');
if (!seoChecks.title_keyword) recommendations.push('Include primary keyword in title');
if (!seoChecks.meta_description) recommendations.push('Optimize meta description (150-160 chars)');
if (!seoChecks.content_length) recommendations.push('Increase content length to at least 800 words');
if (!seoChecks.keyword_density) recommendations.push('Adjust keyword density to 1-3%');
if (!seoChecks.headings) recommendations.push('Improve heading structure (1 H1, multiple H2s)');
if (!seoChecks.internal_links) recommendations.push('Add at least 2 internal links');
if (!seoChecks.images_alt_text) recommendations.push('Add alt text to all images');
if (!seoChecks.url_structure) recommendations.push('Optimize URL slug with primary keyword');
if (!seoChecks.readability) recommendations.push('Improve readability with shorter sentences');

const analysis = {
  ...blogPost,
  seo_score: seoScore,
  max_score: 100,
  seo_grade: seoScore >= 80 ? 'A' : seoScore >= 60 ? 'B' : seoScore >= 40 ? 'C' : 'D',
  seo_checks: seoChecks,
  recommendations: recommendations,
  word_count: wordCount,
  analyzed_at: new Date().toISOString()
};

return [{ json: analysis }];

// Keyword Competition Analysis
const searchResults = $json;
const blogPost = $input.first().json;

let competitorAnalysis = {
  total_results: 0,
  top_competitors: [],
  keyword_difficulty: 'medium',
  content_gaps: [],
  opportunities: []
};

if (searchResults.items) {
  competitorAnalysis.total_results = parseInt(searchResults.searchInformation?.totalResults || 0);

  // Analyze top 10 results
  competitorAnalysis.top_competitors = searchResults.items.slice(0, 5).map(item => ({
    title: item.title,
    url: item.link,
    snippet: item.snippet,
    domain: new URL(item.link).hostname
  }));

  // Determine keyword difficulty
  const totalResults = competitorAnalysis.total_results;
  if (totalResults > 10000000) {
    competitorAnalysis.keyword_difficulty = 'high';
  } else if (totalResults > 1000000) {
    competitorAnalysis.keyword_difficulty = 'medium';
  } else {
    competitorAnalysis.keyword_difficulty = 'low';
  }

  // Identify content gaps
  const competitorTitles = searchResults.items.map(item => item.title.toLowerCase());
  const commonTerms = ['guide', 'tips', 'ideas', 'how to', 'best', 'top'];

  commonTerms.forEach(term => {
    const hasTermCount = competitorTitles.filter(title => title.includes(term)).length;
    if (hasTermCount < 3) {
      competitorAnalysis.content_gaps.push(`Few competitors use "${term}" in titles`);
    }
  });

  // Generate opportunities
  if (competitorAnalysis.keyword_difficulty === 'low') {
    competitorAnalysis.opportunities.push('Low competition - good ranking opportunity');
  }

  if (competitorAnalysis.content_gaps.length > 0) {
    competitorAnalysis.opportunities.push('Content gaps identified for unique positioning');
  }

  // Check for long-tail opportunities
  const longTailKeywords = blogPost.keywords.filter(k => k.split(' ').length >= 3);
  if (longTailKeywords.length > 0) {
    competitorAnalysis.opportunities.push('Long-tail keywords available for targeting');
  }
}

return [{ json: { ...blogPost, competitor_analysis: competitorAnalysis } }];

// Blog Performance Analytics
const searchData = $json;
const blogPost = $input.first().json;

let performanceMetrics = {
  total_clicks: 0,
  total_impressions: 0,
  average_position: 0,
  click_through_rate: 0,
  top_performing_queries: [],
  ranking_keywords: [],
  performance_trend: 'stable'
};

if (searchData.rows && searchData.rows.length > 0) {
  // Calculate totals
  searchData.rows.forEach(row => {
    performanceMetrics.total_clicks += row.clicks || 0;
    performanceMetrics.total_impressions += row.impressions || 0;
  });

  // Calculate averages
  performanceMetrics.average_position = searchData.rows.reduce((sum, row) => 
    sum + (row.position || 0), 0) / searchData.rows.length;

  performanceMetrics.click_through_rate = performanceMetrics.total_impressions > 0 ? 
    (performanceMetrics.total_clicks / performanceMetrics.total_impressions) * 100 : 0;

  // Top performing queries
  performanceMetrics.top_performing_queries = searchData.rows
    .filter(row => row.clicks > 0)
    .sort((a, b) => b.clicks - a.clicks)
    .slice(0, 10)
    .map(row => ({
      query: row.keys[0],
      clicks: row.clicks,
      impressions: row.impressions,
      position: row.position,
      ctr: row.ctr
    }));

  // Keywords ranking in top 10
  performanceMetrics.ranking_keywords = searchData.rows
    .filter(row => row.position <= 10)
    .map(row => ({
      keyword: row.keys[0],
      position: row.position,
      clicks: row.clicks
    }));

  // Performance trend (simplified)
  if (performanceMetrics.average_position <= 5) {
    performanceMetrics.performance_trend = 'excellent';
  } else if (performanceMetrics.average_position <= 10) {
    performanceMetrics.performance_trend = 'good';
  } else if (performanceMetrics.average_position <= 20) {
    performanceMetrics.performance_trend = 'fair';
  } else {
    performanceMetrics.performance_trend = 'needs_improvement';
  }
}

// Generate insights
const insights = [];
if (performanceMetrics.click_through_rate < 2) {
  insights.push('Low CTR - consider improving title and meta description');
}
if (performanceMetrics.average_position > 10) {
  insights.push('Average position below top 10 - focus on content optimization');
}
if (performanceMetrics.ranking_keywords.length < 5) {
  insights.push('Few keywords ranking in top 10 - expand keyword targeting');
}
if (performanceMetrics.total_clicks < 50) {
  insights.push('Low click volume - consider promoting content more actively');
}

return [{ json: { 
  ...blogPost, 
  performance_metrics: performanceMetrics,
  insights: insights,
  last_analyzed: new Date().toISOString()
} }];

// Weekly SEO Report Generator
const reportData = {
  report_period: {
    start: new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
    end: new Date().toISOString().split('T')[0]
  },
  blog_posts_published: Math.floor(Math.random() * 5) + 2, // 2-6 posts
  total_organic_traffic: Math.floor(Math.random() * 1000) + 500,
  avg_seo_score: Math.floor(Math.random() * 20) + 75, // 75-95
  top_performing_posts: [
    {
      title: 'Best Wallpapers for Small Bedrooms',
      clicks: Math.floor(Math.random() * 100) + 50,
      impressions: Math.floor(Math.random() * 1000) + 500,
      avg_position: Math.random() * 5 + 3
    },
    {
      title: 'Modern Furniture Trends 2024',
      clicks: Math.floor(Math.random() * 80) + 40,
      impressions: Math.floor(Math.random() * 800) + 400,
      avg_position: Math.random() * 7 + 4
    }
  ],
  keyword_rankings: {
    improved: Math.floor(Math.random() * 10) + 5,
    declined: Math.floor(Math.random() * 5) + 1,
    new_rankings: Math.floor(Math.random() * 8) + 3
  },
  technical_issues: {
    crawl_errors: Math.floor(Math.random() * 3),
    page_speed_issues: Math.floor(Math.random() * 2),
    mobile_usability: Math.floor(Math.random() * 1)
  },
  recommendations: [
    'Focus on long-tail keywords for better ranking opportunities',
    'Improve internal linking between related blog posts',
    'Optimize images for faster page loading',
    'Create more content around trending interior design topics'
  ]
};

// Calculate week-over-week changes
reportData.traffic_change = ((Math.random() - 0.5) * 30).toFixed(1); // -15% to +15%
reportData.ranking_change = ((Math.random() - 0.5) * 10).toFixed(1); // -5 to +5 positions

return [{ json: reportData }];

