// Content Generator for Social Media
const contentTypes = [
  {
    type: 'product_highlight',
    templates: [
      {
        text: "üåü Product Spotlight: {product_name}\n\n{product_description}\n\n‚ú® Why customers love it:\n‚Ä¢ {benefit_1}\n‚Ä¢ {benefit_2}\n‚Ä¢ {benefit_3}\n\nüõí Shop now: {product_url}\n\n#ecommerce #quality #shopping #deals",
        image_required: true
      }
    ]
  },
  {
    type: 'tips_and_advice',
    templates: [
      {
        text: "üí° Pro Tip Tuesday!\n\n{tip_title}\n\n{tip_description}\n\nTry this and let us know how it works for you! üëá\n\n#tips #advice #ecommerce #business #growth",
        image_required: false
      }
    ]
  },
  {
    type: 'behind_the_scenes',
    templates: [
      {
        text: "üé¨ Behind the Scenes\n\n{bts_description}\n\nWe love sharing our journey with you! What would you like to see more of?\n\n#behindthescenes #team #company #transparency",
        image_required: true
      }
    ]
  },
  {
    type: 'customer_testimonial',
    templates: [
      {
        text: "‚≠ê Customer Love Alert! ‚≠ê\n\n\"{testimonial_text}\"\n\n- {customer_name}\n\nThank you for trusting us! üôè\n\n#testimonial #customerreview #happy #grateful #quality",
        image_required: false
      }
    ]
  },
  {
    type: 'promotional',
    templates: [
      {
        text: "üî• LIMITED TIME OFFER! üî•\n\n{offer_description}\n\nüí∞ Save {discount_amount}\n‚è∞ Ends {end_date}\nüéØ Code: {promo_code}\n\n[Shop Now]({shop_url})\n\n#sale #discount #limitedtime #deals #shopping",
        image_required: true
      }
    ]
  }
];

const tips = [
  {
    title: "Optimize Your Product Photos",
    description: "Use natural lighting and multiple angles to showcase your products. Good photos can increase conversion rates by up to 30%!"
  },
  {
    title: "Write Compelling Product Descriptions",
    description: "Focus on benefits, not just features. Tell a story about how your product solves problems or improves lives."
  },
  {
    title: "Leverage Social Proof",
    description: "Display customer reviews, ratings, and testimonials prominently. 92% of consumers read reviews before making a purchase."
  },
  {
    title: "Optimize for Mobile",
    description: "Over 60% of online shopping happens on mobile devices. Ensure your site loads fast and is easy to navigate on phones."
  },
  {
    title: "Use Abandoned Cart Recovery",
    description: "Send follow-up emails to customers who left items in their cart. This can recover 10-15% of lost sales."
  }
];

const testimonials = [
  {
    text: "Amazing quality and fast shipping! Will definitely order again.",
    customer: "Sarah M."
  },
  {
    text: "Best customer service I've experienced. They went above and beyond!",
    customer: "Mike R."
  },
  {
    text: "The product exceeded my expectations. Highly recommend!",
    customer: "Jennifer L."
  }
];

// Select content type based on time of day
const hour = new Date().getHours();
let selectedType;

if (hour === 9) {
  selectedType = contentTypes.find(c => c.type === 'tips_and_advice');
} else if (hour === 15) {
  selectedType = contentTypes.find(c => c.type === 'product_highlight');
} else if (hour === 18) {
  selectedType = contentTypes.find(c => c.type === 'customer_testimonial');
}

let content = {};

if (selectedType.type === 'tips_and_advice') {
  const tip = tips[Math.floor(Math.random() * tips.length)];
  content = {
    type: 'tips_and_advice',
    text: selectedType.templates[0].text
      .replace('{tip_title}', tip.title)
      .replace('{tip_description}', tip.description),
    image_required: false,
    platforms: ['facebook', 'instagram', 'linkedin']
  };
} else if (selectedType.type === 'customer_testimonial') {
  const testimonial = testimonials[Math.floor(Math.random() * testimonials.length)];
  content = {
    type: 'customer_testimonial',
    text: selectedType.templates[0].text
      .replace('{testimonial_text}', testimonial.text)
      .replace('{customer_name}', testimonial.customer),
    image_required: false,
    platforms: ['facebook', 'instagram', 'twitter']
  };
} else {
  // Product highlight
  content = {
    type: 'product_highlight',
    text: "üåü Check out our amazing products!\n\nQuality you can trust, prices you'll love.\n\nüõí Shop now: https://yourstore.com\n\n#ecommerce #quality #shopping #deals",
    image_required: true,
    platforms: ['facebook', 'instagram']
  };
}

return [{ json: content }];

