    {
      "parameters": {
        "functionCode": "// Social Media Engagement Handler\nconst engagement = $json;\nlet response = '';\n\n// Auto-response templates\nconst responses = {\n  comment: {\n    positive: [\n      'Thank you so much! üòä We really appreciate your support!',\n      'You made our day! üåü Thank you for the kind words!',\n      'We\'re so happy you love it! üíï Thank you!',\n      'Your support means everything to us! üôè Thank you!'\n    ],\n    question: [\n      'Great question! We\'ll send you a DM with more details üì©',\n      'Thanks for asking! Check your DMs for more info üí¨',\n      'We\'d love to help! Sending you a message now üì®'\n    ],\n    general: [\n      'Thanks for engaging with us! üòä',\n      'We appreciate your comment! üíô',\n      'Thank you for being part of our community! üåü'\n    ]\n  },\n  mention: {\n    positive: [\n      'Thank you for the mention! üôå We\'re grateful for customers like you!',\n      'You\'re amazing! üí´ Thanks for spreading the word!',\n      'We\'re honored by your mention! üåü Thank you!'\n    ],\n    general: [\n      'Thanks for the mention! üòä',\n      'We appreciate you thinking of us! üíô'\n    ]\n  }\n};\n\n// Determine response type\nlet responseType = 'general';\nif (engagement.text) {\n  const text = engagement.text.toLowerCase();\n  if (text.includes('love') || text.includes('great') || text.includes('amazing') || text.includes('awesome')) {\n    responseType = 'positive';\n  } else if (text.includes('?') || text.includes('how') || text.includes('when') || text.includes('where')) {\n    responseType = 'question';\n  }\n}\n\n// Select appropriate response\nconst responseCategory = responses[engagement.type] || responses.comment;\nconst responseArray = responseCategory[responseType] || responseCategory.general;\nresponse = responseArray[Math.floor(Math.random() * responseArray.length)];\n\nreturn [{\n  json: {\n    ...engagement,\n    auto_response: response,\n    response_type: responseType,\n    should_respond: true,\n    processed_at: new Date().toISOString()\n  }\n}];"
      },
      "name": "Process Engagement",
      "type": "n8n-nodes-base.function",
      "typeVersion": 1,
      "position": [
        460,
        600
      ]
    },
    {
      "parameters": {
        "authentication": "oAuth2",
        "sendTo": "social@yourstore.com",
        "subject": "üîî New Social Media Engagement",
        "message": "=**New {{ $json.type }} on {{ $json.platform }}**\n\n**From**: {{ $json.user_name || 'Unknown User' }}\n**Content**: {{ $json.text }}\n**Link**: {{ $json.link }}\n\n**Suggested Response**: {{ $json.auto_response }}\n\n**Action Required**: {{ $json.response_type === 'question' ? 'Manual response recommended for question' : 'Auto-response suggested' }}\n\n---\nProcessed at: {{ $json.processed_at }}",
        "options": {}
      },
      "name": "Notify Team",
      "type": "n8n-nodes-base.gmail",
      "typeVersion": 1,
